<?php// Find a bank with a given endpoint.if(!isset($_GET['endpoint'])){		// Endpoint is required.	error('field/required','endpoint');	}// Get the endpoint:$endpoint=preg_replace(VALID_DOMAIN,'',strtolower(trim($_GET['endpoint'])));if($endpoint==''){	// This is an error condition; no valid endpoint provided.	error('field/invalid','endpoint');}// Get the row now:$result=$dz->get_row('select `Name` from `Root.Entities` where `Endpoint`="'.escape($endpoint).'"');if(!$result){	// Not found.	error('entity/notfound');}// Output a JSON row:echo json_encode($result);?>